import{Component,Injector,Input}from"@angular/core";import{NgControl}from"@angular/forms";import{AbstractLookupSelectComponent}from"./abstract-lookup-select.component";export class LookupSelectComponent extends AbstractLookupSelectComponent{constructor(o){super(o),this.maxResultCount=1e3,this.datas=[],this.ngControl=o.get(NgControl),this.ngControl.valueAccessor=this}get isInvalid(){return this.ngControl.dirty&&this.ngControl.invalid}ngOnInit(){this.get()}get(){this.getFn(this.pageQuery).subscribe(({items:o})=>{this.datas=o})}}LookupSelectComponent.decorators=[{type:Component,args:[{selector:"abp-lookup-select",template:'<select\r\n  [id]="cid"\r\n  class="custom-select form-control"\r\n  [(ngModel)]="value"\r\n  [abpDisabled]="disabled"\r\n  [class.input-validation-error]="isInvalid"\r\n>\r\n  <option [ngValue]=""></option>\r\n  <option *ngFor="let data of datas" [ngValue]="data[lookupIdProp]">\r\n    {{ data[lookupNameProp] }}\r\n  </option>\r\n</select>\r\n'}]}],LookupSelectComponent.ctorParameters=()=>[{type:Injector}],LookupSelectComponent.propDecorators={maxResultCount:[{type:Input}]};