import{Component,ChangeDetectionStrategy}from"@angular/core";import{AbstractAccountSettingsService}from"../../abstracts/abstract-account-config.service";import{AccountCaptchaService}from"../../services/account-captcha.service";import{AbstractAccountSettingsComponent}from"../../abstracts/abstract-account-settings.component";export class AccountSettingsCaptchaComponent extends AbstractAccountSettingsComponent{mapTenantSettingsForSubmit(n){return{version:n.version,siteKey:n.siteKey,siteSecret:n.siteSecret}}}AccountSettingsCaptchaComponent.decorators=[{type:Component,args:[{selector:"abp-account-settings-captcha",template:'<ng-container *ngIf="settings$ | async; let settings">\r\n  <div (keyup.enter)="submit(settings)">\r\n    <h4>{{ \'AbpAccount::Captcha\' | abpLocalization }}</h4>\r\n\r\n    <ng-container *ngIf="!isTenant">\r\n      <div class="custom-checkbox custom-control mb-2">\r\n        <input\r\n          type="checkbox"\r\n          id="use-captcha-on-login"\r\n          name="useCaptchaOnLogin"\r\n          class="custom-control-input"\r\n          [(ngModel)]="settings.useCaptchaOnLogin"\r\n          autofocus\r\n        /><label class="custom-control-label" for="use-captcha-on-login">{{\r\n          \'AbpAccount::Description:UseCaptchaOnLogin\' | abpLocalization\r\n        }}</label>\r\n      </div>\r\n      <div class="custom-checkbox custom-control mb-2">\r\n        <input\r\n          type="checkbox"\r\n          id="use-captcha-on-registration"\r\n          name="useCaptchaOnRegistration"\r\n          class="custom-control-input"\r\n          [(ngModel)]="settings.useCaptchaOnRegistration"\r\n        /><label class="custom-control-label" for="use-captcha-on-registration">{{\r\n          \'AbpAccount::Description:UseCaptchaOnRegistration\' | abpLocalization\r\n        }}</label>\r\n      </div>\r\n\r\n      <div class="form-group">\r\n        <label>{{ \'AbpAccount::DisplayName:VerifyBaseUrl\' | abpLocalization }}</label>\r\n        <input type="text" class="form-control" [(ngModel)]="settings.verifyBaseUrl" />\r\n      </div>\r\n    </ng-container>\r\n\r\n    <div class="form-group">\r\n      <label>{{ \'AbpAccount::DisplayName:Version\' | abpLocalization }}</label>\r\n      <input type="text" class="form-control" [(ngModel)]="settings.version" />\r\n    </div>\r\n\r\n    <div class="form-group">\r\n      <label>{{ \'AbpAccount::DisplayName:SiteKey\' | abpLocalization }}</label>\r\n      <input type="text" class="form-control" [(ngModel)]="settings.siteKey" />\r\n    </div>\r\n\r\n    <div class="form-group">\r\n      <label>{{ \'AbpAccount::DisplayName:SiteSecret\' | abpLocalization }}</label>\r\n      <input type="text" class="form-control" [(ngModel)]="settings.siteSecret" />\r\n    </div>\r\n\r\n    <hr class="my-4" />\r\n\r\n    <div>\r\n      <abp-button (click)="submit(settings)" iconClass="fa fa-save" [loading]="loading">{{\r\n        \'AbpAccount::Save\' | abpLocalization\r\n      }}</abp-button>\r\n    </div>\r\n  </div>\r\n</ng-container>\r\n',changeDetection:ChangeDetectionStrategy.OnPush,providers:[{provide:AbstractAccountSettingsService,useClass:AccountCaptchaService}]}]}];