import{Component,ChangeDetectionStrategy}from"@angular/core";import{AccountSettingsService}from"../../services/account-settings.service";import{AbstractAccountSettingsService}from"../../abstracts/abstract-account-config.service";import{AbstractAccountSettingsComponent}from"../../abstracts/abstract-account-settings.component";export class AccountSettingsGeneralComponent extends AbstractAccountSettingsComponent{}AccountSettingsGeneralComponent.decorators=[{type:Component,args:[{selector:"abp-account-settings-general",template:'<ng-container *ngIf="settings$ | async; let settings">\r\n  <div (keyup.enter)="submit(settings)">\r\n    <div class="custom-checkbox custom-control mb-2">\r\n      <input\r\n        type="checkbox"\r\n        id="IsSelfRegistrationEnabled"\r\n        name="IsSelfRegistrationEnabled"\r\n        class="custom-control-input"\r\n        [(ngModel)]="settings.isSelfRegistrationEnabled"\r\n        autofocus\r\n      /><label class="custom-control-label" for="IsSelfRegistrationEnabled">{{\r\n        \'AbpAccount::DisplayName:IsSelfRegistrationEnabled\' | abpLocalization\r\n      }}</label>\r\n    </div>\r\n    <div class="custom-checkbox custom-control mb-2">\r\n      <input\r\n        type="checkbox"\r\n        id="EnableLocalLogin"\r\n        name="EnableLocalLogin"\r\n        class="custom-control-input"\r\n        [(ngModel)]="settings.enableLocalLogin"\r\n      /><label class="custom-control-label" for="EnableLocalLogin">{{\r\n        \'AbpAccount::DisplayName:EnableLocalLogin\' | abpLocalization\r\n      }}</label>\r\n    </div>\r\n  </div>\r\n\r\n  <hr class="my-4" />\r\n\r\n  <div>\r\n    <abp-button (click)="submit(settings)" iconClass="fa fa-save" [loading]="loading">{{\r\n      \'AbpAccount::Save\' | abpLocalization\r\n    }}</abp-button>\r\n  </div>\r\n</ng-container>\r\n',changeDetection:ChangeDetectionStrategy.OnPush,providers:[{provide:AbstractAccountSettingsService,useClass:AccountSettingsService}]}]}];