import{Component,ChangeDetectionStrategy}from"@angular/core";import{AccountTwoFactorSettingService}from"../../services/account-two-factor-settings.service";import{AbstractAccountSettingsService}from"../../abstracts/abstract-account-config.service";import{AbstractAccountSettingsComponent}from"../../abstracts/abstract-account-settings.component";import{eTwoFactorBehaviour,twoFactorBehaviourOptions}from"../../enums/two-factor-behaviour";export class AccountSettingsTwoFactorComponent extends AbstractAccountSettingsComponent{constructor(){super(...arguments),this.eTwoFactorBehaviour=eTwoFactorBehaviour,this.twoFactorBehaviourOptions=twoFactorBehaviourOptions}}AccountSettingsTwoFactorComponent.decorators=[{type:Component,args:[{selector:"abp-account-settings-two-factor",template:'<ng-container *ngIf="settings$ | async; let settings">\r\n  <div (keyup.enter)="submit(settings)">\r\n    <h4>{{ \'AbpAccount::TwoFactorAuthentication\' | abpLocalization }}</h4>\r\n    <div>\r\n      <div class="form-group">\r\n        <label for="AccountTwoFactorSettings_TwoFactorBehaviour">{{\r\n          \'AbpIdentity::DisplayName:Abp.Identity.TwoFactorBehaviour\' | abpLocalization\r\n        }}</label>\r\n        <select\r\n          name="TwoFactorBehaviour"\r\n          class="custom-select form-control"\r\n          [(ngModel)]="settings.twoFactorBehaviour"\r\n        >\r\n          <option *ngFor="let option of twoFactorBehaviourOptions" [ngValue]="option.value">{{\r\n            option.key\r\n          }}</option>\r\n        </select>\r\n      </div>\r\n      <div\r\n        *ngIf="settings.twoFactorBehaviour === eTwoFactorBehaviour.Optional"\r\n        class="custom-checkbox custom-control mb-2"\r\n      >\r\n        <input\r\n          name="UsersCanChange"\r\n          type="checkbox"\r\n          checked="checked"\r\n          id="AccountTwoFactorSettings_UsersCanChange"\r\n          [(ngModel)]="settings.usersCanChange"\r\n          class="custom-control-input"\r\n        /><label class="custom-control-label" for="AccountTwoFactorSettings_UsersCanChange">{{\r\n          \'AbpIdentity::DisplayName:Abp.Identity.UsersCanChange\' | abpLocalization\r\n        }}</label>\r\n      </div>\r\n      <div class="custom-checkbox custom-control mb-2">\r\n        <input\r\n          name="IsRememberBrowserEnabled"\r\n          type="checkbox"\r\n          checked="checked"\r\n          id="AccountTwoFactorSettings_IsRememberBrowserEnabled"\r\n          [(ngModel)]="settings.isRememberBrowserEnabled"\r\n          class="custom-control-input "\r\n        /><label\r\n          class="custom-control-label"\r\n          for="AccountTwoFactorSettings_IsRememberBrowserEnabled"\r\n          >{{ \'AbpAccount::RememberBrowser\' | abpLocalization }}</label\r\n        >\r\n      </div>\r\n    </div>\r\n    <hr class="my-4" />\r\n    <div>\r\n      <abp-button (click)="submit(settings)" iconClass="fa fa-save" [loading]="loading">{{\r\n        \'AbpAccount::Save\' | abpLocalization\r\n      }}</abp-button>\r\n    </div>\r\n  </div>\r\n</ng-container>\r\n',changeDetection:ChangeDetectionStrategy.OnPush,providers:[{provide:AbstractAccountSettingsService,useClass:AccountTwoFactorSettingService}]}]}];