import{TrackByService}from"@abp/ng.core";import{ChangeDetectionStrategy,ChangeDetectorRef,Component}from"@angular/core";export class EntityChangeModalComponent{constructor(e,n){this.cdRef=e,this.track=n,this.history=[],this.visible=!1}}EntityChangeModalComponent.decorators=[{type:Component,args:[{selector:"abp-entity-change-modal",template:'<abp-modal [(visible)]="visible">\r\n  <ng-template #abpHeader>\r\n    <h5 class="modal-title">\r\n      {{ entityTypeFullName }}\r\n      <br />\r\n      <span style="font-size:small">({{ entityId }})</span>\r\n    </h5>\r\n  </ng-template>\r\n  <ng-template #abpBody>\r\n    <ng-container *ngIf="history.length; else noChanges">\r\n      <abp-entity-change-details\r\n        *ngFor="let change of history; first as f; trackBy: track.byDeep(\'entityChange\', \'id\')"\r\n        [itemWithUserName]="change"\r\n        [isCollapsed]="!f"\r\n      ></abp-entity-change-details>\r\n    </ng-container>\r\n\r\n    <ng-template #noChanges\r\n      ><div class="text-center">\r\n        {{ \'AbpAuditLogging::NoChanges\' | abpLocalization }}\r\n      </div></ng-template\r\n    >\r\n  </ng-template>\r\n  <ng-template #abpFooter>\r\n    <button class="btn btn-secondary" #abpClose>\r\n      {{ \'AbpAuditLogging::Close\' | abpLocalization }}\r\n    </button>\r\n  </ng-template>\r\n</abp-modal>\r\n',changeDetection:ChangeDetectionStrategy.OnPush}]}],EntityChangeModalComponent.ctorParameters=()=>[{type:ChangeDetectorRef},{type:TrackByService}];