import{Component,ChangeDetectionStrategy,Input}from"@angular/core";import{IdentityServerModalTabComponent}from"../../../../modals/identity-server-modal/tabs/identity-server-modal-tab.component";export class ClientsModalGrantTypesTabComponent extends IdentityServerModalTabComponent{constructor(){super(...arguments),this.selected=[],this.title="GrantTypes",this.customModel="Custom",this.grantTypesOptions=["implicit","authorization_code","hybrid","client_credentials","password",this.customModel],this.customModelValue="",this.trackByFn=t=>t}add(){const t=this.model===this.customModel?this.customModelValue:this.model;this.selected.includes(t)||(this.selected.push(t),this.customModelValue="")}getValue(){return{allowedGrantTypes:this.selected}}delete(t){this.selected.splice(t,1)}}ClientsModalGrantTypesTabComponent.decorators=[{type:Component,args:[{selector:"abp-clients-modal-grant-types-tab",template:'<ng-template>\r\n  <h3>{{ \'AbpIdentityServer::\' + title | abpLocalization }}</h3>\r\n  <div class="pt-3 pb-5 clearfix">\r\n    <form (submit)="add()">\r\n      <div class="row">\r\n        <div class="col-12">\r\n          <div class="form-group">\r\n            <label for="sample-propert-grant-type">Grant Type </label>\r\n            <select\r\n              class="form-control"\r\n              id="sample-propert-grant-type"\r\n              name="sample-propert-grant-type"\r\n              [(ngModel)]="model"\r\n            >\r\n              <option *ngFor="let option of grantTypesOptions" [ngValue]="option">\r\n                {{ option }}\r\n              </option>\r\n            </select>\r\n          </div>\r\n        </div>\r\n        <div class="col-12" *ngIf="model === customModel">\r\n          <div class="form-group">\r\n            <label for="sample-propert-custom-grant-type">Grant Type </label>\r\n            <input\r\n              type="text"\r\n              id="sample-propert-custom-grant-type"\r\n              name="sample-propert-custom-grant-type"\r\n              class="form-control"\r\n              [placeholder]="\'AbpIdentityServer::CustomGrantType\' | abpLocalization"\r\n              [(ngModel)]="customModelValue"\r\n            />\r\n          </div>\r\n        </div>\r\n      </div>\r\n      <button id="add-new-property" class="float-right mb-2 btn btn-success" type="submit">\r\n        <i class="fa fa-plus"></i> {{ \'AbpIdentityServer::AddNew\' | abpLocalization }}\r\n      </button>\r\n    </form>\r\n  </div>\r\n\r\n  <div *ngIf="selected?.length" class="table-responsive-sm">\r\n    <table id="property-table" class="table table-striped">\r\n      <thead>\r\n        <th scope="col">Grant Type</th>\r\n        <th scope="col"></th>\r\n      </thead>\r\n      <tbody>\r\n        <tr *ngFor="let row of selected; trackBy: trackByFn; let i = index">\r\n          <td>{{ row }}</td>\r\n          <td>\r\n            <button type="button" class="btn btn-outline-danger float-right" (click)="delete(i)">\r\n              <i class="fa fa-trash"></i>\r\n            </button>\r\n          </td>\r\n        </tr>\r\n      </tbody>\r\n    </table>\r\n  </div>\r\n</ng-template>\r\n',providers:[{provide:IdentityServerModalTabComponent,useExisting:ClientsModalGrantTypesTabComponent}],changeDetection:ChangeDetectionStrategy.OnPush}]}],ClientsModalGrantTypesTabComponent.propDecorators={selected:[{type:Input}]};