import{ListService}from"@abp/ng.core";import{Component,Input}from"@angular/core";import{IdentityUserService}from"../../../proxy/identity/identity-user.service";export class OrganizationMembersModalBodyComponent{constructor(t,e){this.list=t,this.userService=e,this.allUnits={items:[]}}ngOnInit(){this.hookToQuery()}hookToQuery(){this.list.hookToQuery(t=>this.userService.getList(t)).subscribe(t=>{this.allUnits=t})}}OrganizationMembersModalBodyComponent.decorators=[{type:Component,args:[{selector:"abp-organization-members-modal-body",template:'\n    <div id="data-tables-table-filter" class="data-tables-filter">\n      <div class="input-group">\n        <input\n          type="search"\n          class="form-control"\n          [placeholder]="\'AbpUi::PagerSearch\' | abpLocalization"\n          [(ngModel)]="list.filter"\n        />\n        <div class="input-group-append">\n          <button class="btn btn-sm btn-primary" (click)="list.get()">\n            <i class="fas fa-search"></i>\n          </button>\n        </div>\n      </div>\n    </div>\n\n    <div class="card">\n      <abp-extensible-table\n        actionsText=""\n        [data]="allUnits.items"\n        [recordsTotal]="allUnits.totalCount"\n        [actionsColumnWidth]="38"\n        [actionsTemplate]="customAction"\n        [list]="list"\n      ></abp-extensible-table>\n    </div>\n\n    <ng-template #customAction let-row>\n      <input\n        type="checkbox"\n        [(ngModel)]="checkedUnits[row.id]"\n        [disabled]="isCheckboxDisabled(row.id)"\n      />\n    </ng-template>\n  ',providers:[ListService]}]}],OrganizationMembersModalBodyComponent.ctorParameters=()=>[{type:ListService},{type:IdentityUserService}],OrganizationMembersModalBodyComponent.propDecorators={checkedUnits:[{type:Input}],isCheckboxDisabled:[{type:Input}]};