import{ListService}from"@abp/ng.core";import{Component,Input}from"@angular/core";import{IdentityRoleService}from"../../../proxy/identity/identity-role.service";export class OrganizationRolesModalBodyComponent{constructor(t,e){this.list=t,this.roleService=e,this.allUnits={items:[]}}ngOnInit(){this.hookToQuery()}hookToQuery(){this.list.hookToQuery(t=>this.roleService.getList(t)).subscribe(t=>{this.allUnits=t})}}OrganizationRolesModalBodyComponent.decorators=[{type:Component,args:[{selector:"abp-organization-roles-modal-body",template:'\n    <div class="card">\n      <abp-extensible-table\n        actionsText=""\n        [data]="allUnits.items"\n        [recordsTotal]="allUnits.totalCount"\n        [actionsColumnWidth]="38"\n        [actionsTemplate]="customAction"\n        [list]="list"\n      ></abp-extensible-table>\n    </div>\n\n    <ng-template #customAction let-row>\n      <input\n        type="checkbox"\n        [(ngModel)]="checkedUnits[row.id]"\n        [disabled]="isCheckboxDisabled(row.id)"\n      />\n    </ng-template>\n  ',providers:[ListService]}]}],OrganizationRolesModalBodyComponent.ctorParameters=()=>[{type:ListService},{type:IdentityRoleService}],OrganizationRolesModalBodyComponent.propDecorators={checkedUnits:[{type:Input}],isCheckboxDisabled:[{type:Input}]};