import{Component,Injector}from"@angular/core";import{AbstractTemplateContentComponent}from"../abstract-template-content/abstract-template-content.component";import{Router}from"@angular/router";export class InlineTemplateContentComponent extends AbstractTemplateContentComponent{constructor(n,t){super(n),this.injector=n,this.router=t}customizePerCulture(){this.router.navigateByUrl(`/text-template-management/text-templates/contents/${this.templateContent.name}`)}save(){super.save(()=>{this.router.navigateByUrl("/text-template-management/text-templates")})}}InlineTemplateContentComponent.decorators=[{type:Component,args:[{selector:"abp-inline-template-content",template:'<div class="row entry-row">\r\n  <div class="col-auto">\r\n    <h1 class="content-header-title">\r\n      {{ \'TextTemplateManagement::Contents\' | abpLocalization }}\r\n    </h1>\r\n  </div>\r\n  <div class="col-lg-auto pl-lg-0">\r\n    <abp-breadcrumb></abp-breadcrumb>\r\n  </div>\r\n</div>\r\n\r\n<div\r\n  role="alert"\r\n  class="alert alert-danger"\r\n  [innerHTML]="\'TextTemplateManagement::InlineContentDescription\' | abpLocalization"\r\n></div>\r\n\r\n<div class="card">\r\n  <div class="card-header">\r\n    <label\r\n      >{{ \'TextTemplateManagement::Name\' | abpLocalization }}:\r\n      <strong>{{ templateContent.name }}</strong></label\r\n    >\r\n  </div>\r\n  <div class="card-body">\r\n    <form id="template-inline-content-form" [formGroup]="form">\r\n      <div class="row">\r\n        <div class="col">\r\n          <div class="form-group">\r\n            <label for="TemplateContent">{{\r\n              \'TextTemplateManagement::TemplateContent\' | abpLocalization\r\n            }}</label\r\n            ><span> * </span\r\n            ><textarea\r\n              id="TemplateContent"\r\n              name="TemplateContent"\r\n              rows="10"\r\n              class="form-control"\r\n              formControlName="content"\r\n              [disabled]="busy"\r\n            >\r\n            </textarea>\r\n          </div>\r\n        </div>\r\n      </div>\r\n      <div class="row">\r\n        <div class="col col-md-4">\r\n          <a\r\n            routerLink="/text-template-management/text-templates"\r\n            role="button"\r\n            class="btn btn-primary"\r\n          >\r\n            <i class="fa fa-arrow-left mr-1"></i>\r\n            <span>{{ \'TextTemplateManagement::ReturnToTemplates\' | abpLocalization }}</span>\r\n          </a>\r\n        </div>\r\n        <div class="col col-md-8">\r\n          <div class="float-right">\r\n            <button\r\n              id="save-content"\r\n              class="btn btn-primary"\r\n              type="button"\r\n              [disabled]="busy"\r\n              (click)="save()"\r\n            >\r\n              <span>{{ \'TextTemplateManagement::SaveContent\' | abpLocalization }}</span>\r\n            </button>\r\n            <button\r\n              id="restore-to-default"\r\n              class="btn btn-danger ml-1"\r\n              type="button"\r\n              [disabled]="busy"\r\n              (click)="restoreToDefault()"\r\n            >\r\n              <span>{{ \'TextTemplateManagement::RestoreToDefault\' | abpLocalization }}</span>\r\n            </button>\r\n            <button\r\n              id="edit-unique-localization"\r\n              class="btn btn-link ml-1"\r\n              type="button"\r\n              [disabled]="busy"\r\n              (click)="customizePerCulture()"\r\n            >\r\n              <span>{{ \'TextTemplateManagement::CustomizePerCulture\' | abpLocalization }}</span>\r\n            </button>\r\n          </div>\r\n        </div>\r\n      </div>\r\n    </form>\r\n  </div>\r\n</div>\r\n'}]}],InlineTemplateContentComponent.ctorParameters=()=>[{type:Injector},{type:Router}];