import{Component,Inject,Input}from"@angular/core";import{PROFILE_PICTURE}from"@volo/abp.commercial.ng.ui";import{BehaviorSubject}from"rxjs";export class CurrentUserImageComponent{constructor(e){this.profilePicture$=e}}CurrentUserImageComponent.decorators=[{type:Component,args:[{selector:"abp-current-user-image",template:'\n    <ng-container *ngIf="profilePicture$ | async as pP">\n      <ng-container [ngSwitch]="pP.type">\n        <img *ngSwitchCase="\'image\'" [src]="pP.source" alt="user" [ngClass]="classes" />\n        <i *ngSwitchCase="\'icon\'" [ngClass]="pP.source + \' \' + classes"></i>\n      </ng-container>\n    </ng-container>\n  ',styles:["i.fas.user-avatar,i.fas.user-avatar-big{align-items:center;display:flex;justify-content:center}"]}]}],CurrentUserImageComponent.ctorParameters=()=>[{type:BehaviorSubject,decorators:[{type:Inject,args:[PROFILE_PICTURE]}]}],CurrentUserImageComponent.propDecorators={classes:[{type:Input}]};