import{Component}from"@angular/core";export class HttpErrorComponent{constructor(){this.errorStatus="401"}get errorStatusText(){switch(this.errorStatus){case"401":return"AbpUi::401Message";case"403":return"AbpUi::403Message";case"404":return"AbpUi::404Message";case"500":return"AbpUi::500Message";default:return""}}get errorDetail(){switch(this.errorStatus){case"401":return"AbpUi::DefaultErrorMessage401Detail";case"403":return"AbpUi::DefaultErrorMessage403Detail";case"404":return"AbpUi::DefaultErrorMessage404Detail";case"500":return"AbpUi::DefaultErrorMessage";default:return""}}}HttpErrorComponent.decorators=[{type:Component,args:[{selector:"abp-http-error",template:'<div class="error-page-container">\r\n  <div class="row">\r\n    <div class="col col-auto">\r\n      <div class="status-icon">\r\n        <i class="fa fa-frown-o text-danger" aria-hidden="true"></i>\r\n        <span></span>\r\n      </div>\r\n    </div>\r\n    <div class="col">\r\n      <div class="status-content">\r\n        <h1>{{ errorStatus }}</h1>\r\n        <h2 class="text-danger mb-0">\r\n          {{ errorStatusText | abpLocalization }}<small class="text-muted">{{ errorDetail | abpLocalization }}</small>\r\n        </h2>\r\n        <p class="mt-3 mb-4"></p>\r\n        <a routerLink="/" class="btn btn-primary">{{ \'AbpUi::GoHomePage\' | abpLocalization }}</a>\r\n        <a href="javascript:void(0)" class="mt-4 mb-1 d-block" (click)="destroy$.next()"\r\n          ><i class="fa fa-long-arrow-left mr-1"></i>{{ \'AbpUi::GoBack\' | abpLocalization }}</a\r\n        >\r\n      </div>\r\n    </div>\r\n  </div>\r\n</div>\r\n'}]}];