import{Component}from"@angular/core";export class FullScreenComponent{get fullscreen(){return window.innerHeight===screen.height}toggleFullscreen(){const e=document.documentElement;document.fullscreenElement||document.mozFullScreenElement||document.webkitFullscreenElement||document.msFullscreenElement?document.exitFullscreen?document.exitFullscreen():document.msExitFullscreen?document.msExitFullscreen():document.mozCancelFullScreen?document.mozCancelFullScreen():document.webkitExitFullscreen&&document.webkitExitFullscreen():e.requestFullscreen?e.requestFullscreen():e.msRequestFullscreen?e.msRequestFullscreen():e.mozRequestFullScreen?e.mozRequestFullScreen():e.webkitRequestFullscreen&&e.webkitRequestFullscreen(Element.ALLOW_KEYBOARD_INPUT)}}FullScreenComponent.decorators=[{type:Component,args:[{selector:"abp-full-screen",template:'\n    <a\n      href="javascript:void(0);"\n      class="btn"\n      id="btnFullscreen"\n      (click)="toggleFullscreen()"\n      [class.expanded]="fullscreen"\n    >\n      <i class="fas fa-expand"></i>\n      <i class="fas fa-compress"></i>\n    </a>\n  '}]}];