import{Component,ViewEncapsulation}from"@angular/core";import{PageAlertService}from"@abp/ng.theme.shared";export class PageAlertContainerComponent{constructor(e){this.service=e}}PageAlertContainerComponent.decorators=[{type:Component,args:[{selector:"abp-page-alert-container",template:'<ng-container *ngFor="let alert of service.alerts$ | async; let i = index">\r\n  <div\r\n    class="alert alert-{{ alert.type }} fade show"\r\n    [ngClass]="{ \'alert-dismissible fade show\': alert.dismissible }"\r\n    role="alert"\r\n  >\r\n    <h4 class="alert-heading" *ngIf="alert.title">\r\n      {{ alert.title | abpLocalization: alert.titleLocalizationParams }}\r\n    </h4>\r\n    {{ alert.message | abpLocalization: alert.messageLocalizationParams }}\r\n    <button\r\n      *ngIf="alert.dismissible"\r\n      type="button"\r\n      class="close"\r\n      data-dismiss="alert"\r\n      aria-label="Close"\r\n      (click)="service.remove(i)"\r\n    >\r\n      <span aria-hidden="true">&times;</span>\r\n    </button>\r\n  </div>\r\n</ng-container>\r\n',encapsulation:ViewEncapsulation.None}]}],PageAlertContainerComponent.ctorParameters=()=>[{type:PageAlertService}];