import{ChangeDetectionStrategy,Component,ViewEncapsulation}from"@angular/core";import{ValidationErrorComponent as ErrorComponent}from"@ngx-validate/core";export class ValidationErrorComponent extends ErrorComponent{get abpErrors(){return this.errors&&this.errors.length?this.errors.map(r=>{if(!r.message)return r;const e=r.message.indexOf("[");return e>-1?Object.assign(Object.assign({},r),{message:r.message.slice(0,e),interpoliteParams:r.message.slice(e+1,r.message.length-1).split(",")}):r}):[]}}ValidationErrorComponent.decorators=[{type:Component,args:[{selector:"abp-validation-error",template:'\n    <span\n      class="field-validation-error text-danger"\n      data-valmsg-for="Role.Name"\n      data-valmsg-replace="true"\n      ><span *ngFor="let error of abpErrors; trackBy: trackByFn">\n        {{ error.message | abpLocalization: error.interpoliteParams }}</span\n      ></span\n    >\n  ',changeDetection:ChangeDetectionStrategy.OnPush,encapsulation:ViewEncapsulation.None,styles:["\n      .custom-checkbox + * .field-validation-error span,\n      ngb-timepicker + * .field-validation-error span {\n        background: transparent !important;\n        border: 0 !important;\n        color: currentColor !important;\n        padding: 0 !important;\n      }\n    "]}]}];