import{NavItemsService}from"@abp/ng.theme.shared";import{APP_INITIALIZER}from"@angular/core";import{CurrentUserComponent}from"../components/nav-items/current-user.component";import{FullScreenComponent}from"../components/nav-items/full-screen.component";import{LanguagesComponent}from"../components/nav-items/languages.component";export const LEPTON_THEME_NAV_ITEM_PROVIDERS=[{provide:APP_INITIALIZER,useFactory:configureNavItems,deps:[NavItemsService],multi:!0}];export function configureNavItems(e){return()=>{e.addItems([{id:"Theme.LanguagesComponent",order:100,component:LanguagesComponent},{id:"Theme.CurrentUserComponent",order:100,component:CurrentUserComponent},{id:"Theme.FullScreenComponent",order:100,component:FullScreenComponent}])}}